import { useState } from "react";
import Product from "./components/Product";
import Button from "./components/Button"
// import Footer from "./components/Footer";
// import React from "react";


const App = ()=> {
  const [Key, setKey] = useState("");
  const [minPrice, setMinPrice] = useState(0);
  const [maxPrice, setMaxPrice] = useState(Infinity);
  const [Urut, setUrut] = useState("id");
  const [sortOrder, setSortOrder] = useState("asc");
  const [Halaman, setHalaman] = useState(1);
  const [editedProduct, setEditedProduct] = useState();

  
  const [products, setProducts] = useState([
    {
      id: 1,
      name: "MacBook Air 15”",
      image: "https://i.guim.co.uk/img/media/2f9723cd3b3a3604c054f7c6dfad938da1da1e16/781_631_4115_2469/master/4115.jpg?width=1200&height=900&quality=85&auto=format&fit=crop&s=f95c10e402d13755b67dc1fad6ff9f6d",
      price: 26999999,
    },
    {
      id: 2,
      name: "iPhone 14 Pro",
      image: "data:image/jpeg;base64,",
      price: 19999999,
    },
    {
      id: 3,
      name: "iPhone 14",
      image: "data:image/jpeg;base64,",
      price: 15999999,
    },
    {
      id: 4,
      name: "Apple Vision Pro",
      image: "data:image/jpeg;base64,",
      price: 66999999,
    },
    {
      id: 5,
      name: "Apple Watch Series 8",
      image: "data:image/jpeg;base64,",
      price: 7999999,
    },
    {
      id: 6,
      name: "iPad Pro",
      image: "https://p-id.ipricegroup.com/uploaded_fccab05c8f8c24e6280ca9c4e5e927ec.jpg",
      price: 15999999,
    },
    {
      id: 7,
      name: "MacBook Air 15”",
      image: "https://i.guim.co.uk/img/media/2f9723cd3b3a3604c054f7c6dfad938da1da1e16/781_631_4115_2469/master/4115.jpg?width=1200&height=900&quality=85&auto=format&fit=crop&s=f95c10e402d13755b67dc1fad6ff9f6d",
      price: 26999999,
    },
    {
      id: 8,
      name: "iPhone 14 Pro",
      image: "data:image/jpeg;base64,",
      price: 19999999,
    },
    {
      id: 9,
      name: "iPhone 14",
      image: "data:image/jpeg;base64,",
      price: 15999999,
    },
    {
      id: 10,
      name: "Apple Vision Pro",
      image: "data:image/jpeg;base64,",
      price: 66999999,
    },
    {
      id: 11,
      name: "Apple Watch Series 8",
      image: "data:image/jpeg;base64,",
      price: 7999999,
    },
    {
      id: 12,
      name: "iPad Pro",
      image: "https://p-id.ipricegroup.com/uploaded_fccab05c8f8c24e6280ca9c4e5e927ec.jpg",
      price: 15999999,
    },
  ]);


  const filteredSortedProducts = products
    .toSorted((a, b) => {
      if (sortOrder === "asc") {
        return a[Urut] < b[Urut] ? -1 : 1;
      } else {
        return a[Urut] > b[Urut] ? -1 : 1;
      }
    })
    .filter(
      (product) =>
        product.name.toLowerCase().includes(Key) &&
        product.price >= minPrice &&
        product.price <= maxPrice
    );

  return (
    <>
    <div className="products">
      <header>
        <label>
          Cari:
          <input
            type="text"
            value={Key}
            onChange={(e) => setKey(e.target.value)}
          />
        </label>
      
        <section>
          Harga:
          <label>
            Minimal:
            <input
              type="number"
              value={minPrice}
              onChange={(e) => setMinPrice(e.target.value)}
            />
          </label>
          <label>
            Maksimal:
            <input
              type="number"
              value={maxPrice}
              onChange={(e) => setMaxPrice(e.target.value || Infinity)}
            />
          </label>
        </section>
      
        <section>
          Urutkan:
          <select value={Urut} onChange={(e) => setUrut(e.target.value)}>
            <option value="id">Normal</option>
            <option value="name">Nama</option>
            <option value="price">Harga</option>
          </select>

          <select
            value={sortOrder}
            onChange={(e) => setSortOrder(e.target.value)}
          >
            <option value="asc">Naik</option>
            <option value="desc">Turun</option>
          </select>
        </section>
      </header>

      <main>
        {filteredSortedProducts.length > 0
          ? filteredSortedProducts
              .filter((_product, i) => i >= 4 * Halaman - 4 && i < 4 * Halaman)
              .map((product) => (
                <Product
                  key={product.id}
                  {...product}
                  setEditedProduct={setEditedProduct}
                />
              ))
          : "Produk tidak ada"}
      </main>

      <footer>
        <Button onClick={() => setHalaman(Halaman - 1)} disabled={Halaman === 1}>
          Sebelumnya
        </Button>
        {filteredSortedProducts
          .filter((_product, i) => i % 4 === 0)
          .map((_product, i) => (

            <button
              key={i}
              className="Halaman-number"
              onClick={() => setHalaman(i + 1)}
              disabled={i + 1 === Halaman}
            >
              {i + 1}
            </button>
          ))}

        <Button
          onClick={() => setHalaman(Halaman + 1)}
          disabled={Halaman === Math.ceil(filteredSortedProducts.length / 4)}
        >
          Berikutnya
        </Button>
      </footer>

      {editedProduct && (
        <form
          className="dialog"
          onSubmit={(e) => {
            e.preventDefault();
            setProducts(
              products.map((product) =>
                product.id === editedProduct.id ? editedProduct : product
              )
            );
            setEditedProduct(undefined);
          }}
        >
          
          <h1>Edit Produk</h1>
          <label>
            Nama
            <input
              type="text"
              value={editedProduct.name}
              onChange={(e) =>
                setEditedProduct({ ...editedProduct, name: e.target.value })
              }
              autoFocus
            />
          </label>
  
          <label>
            Harga
            <input
              type="number"
              value={editedProduct.price}
              onChange={(e) =>
                setEditedProduct({
                  ...editedProduct,
                  price: parseInt(e.target.value),
                })
              }
            />
          </label>
  
          <div style={{ display: "flex", justifyContent: "space-between" }}>
            <Button
              type="reset"
              variant="tonal"
              onClick={() => setEditedProduct(undefined)}
            >
              Batal
            </Button>
            <Button>Simpan</Button>
          </div>
        </form>
      )}
    </div>
    </>
  );
}

export default App


